package com.mycompany.eainf_rpg;import javafx.application.Application;import javafx.fxml.FXMLLoader;import javafx.scene.Parent;import javafx.scene.Scene;import javafx.stage.Stage;import java.io.IOException;/** * JavaFX App */public class App extends Application {    private static Scene scene;    private static Player player;                @Override    public void start(Stage stage) throws IOException {                scene = new Scene(loadFXML("Homescreen"), 600, 400);        stage.setScene(scene);        stage.show();    }    static void setRoot(String fxml) throws IOException {        scene.setRoot(loadFXML(fxml));    }    private static Parent loadFXML(String fxml) throws IOException {        FXMLLoader fxmlLoader = new FXMLLoader(App.class.getResource(fxml + ".fxml"));        return fxmlLoader.load();    }    public static void main(String[] args) {                player = new Player(null, 100, 100, null, null, null, null, 1, 10);        launch();    }                         public static Player getPlayer() {        return player;    }    public static void setPlayer(Player player) {        App.player = player;    }    }